import React, {ChangeEvent, useState } from 'react'
import { FaPlus } from 'react-icons/fa'


const AddItem = ({ items }) => {
    const [inputText, setInputText] = useState("");

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        // ðŸ‘‡ Store the input value to local state
        setInputText(e.target.value);
    };
    const label = inputText;
    const [Litems, setLitems] = useState(items);
    const newItem = { id: 5, checked: false, label: label }
    const addToList = () => {
        items.push(newItem);
        setLitems(items);
    }
    return (
        <div className="container">
            <form className='addForm justify-content-center'>
                <div className="input-group border border-primary rounded-3">
                    <label htmlFor='addItem' className="input-group-text border border-end-2 align-middle rounded-start-3">Add Item</label>
                    <input
                        type='text'
                        id='addItem'
                        className="form-control w-50"
                        autoFocus placeholder='Add Item'
                        required
                        value={inputText}
                    />
                    <button type="submit" className="btn btn-primary form-control  rounded-end-2" onSubmit={addToList} aria-label='Add Item'>
                        <FaPlus />
                    </button>
                </div>
            </form>
        </div>
    )
}

export default AddItem
