import React from 'react'
import Title from '../Title'
import { Content } from './Content.js'
import { useState } from 'react'

export const ToDoList = () => {
    const [List_items, setList_items] = useState(
        [
          {
            id: 1,
            checked: true,
            label: "Task 1"
          },
          {
            id: 2,
            checked: true,
            label: "Task 2"
          },
          {
            id: 3,
            checked: false,
            label: "Task 3"
          },
          {
            id: 4,
            checked: true,
            label: "Task 4"
          }
        ]
      );
      const handleCheckBox = (id) => {
        const Listitems = List_items.map((item) => item.id === id ? { ...item, checked: !item.checked } : item);
        setList_items(Listitems);
        localStorage.setItem("todo_list", JSON.stringify(Listitems))
      }
      const removeItem = (id) => {
        const rm = List_items.filter((item) => item.id !== id);
        setList_items(rm);
        localStorage.setItem("todo_list", JSON.stringify(rm))
      }
    return (
        <div>
            <Title title="Task List"/>
            <Content List_items={List_items} handleCheckBox={handleCheckBox} removeItem={removeItem}/>
        </div>
    )
}
