import React from "react";
import "./content.css";
import { useState } from "react";

export const Content = () => {
  const [count, setCount] = useState(0);
  function increaseLikeCount() {
    setCount((like) => {
      return like + 1;
    });
  }
  const [lang, setLang]=useState("React");
  function changeLang(){
    setLang(()=>selectLang());
  }

  function selectLang() {
    let langs = ["React", "Angular", "Vue"];
    let randint = Math.floor(Math.random() * 3);
    return langs[randint];
  }
  return (
    <div>
      <p>Learn {lang}</p>
      <span id="likeButton">
        <button onClick={()=>changeLang()}>Change</button>       
      </span>
      <span id="likeButton">
        <button onClick={()=>increaseLikeCount()}>Like</button>
        <span>{count}</span>
      </span>
    </div>
  );
};
